import React from "react";
import { Link } from "react-router-dom";
import { Button, Segment, Icon } from "semantic-ui-react";

const loginButtonStyle = { 
marginLeft: `auto`,
marginRight: `3px`,
marginTop: `3px`,
marginBottom: `auto`

}

function InfoBar ({isLoggedIn}) {
    return (
    <div className="ui top fixed menu">

        <Button className="circular ui tiny icon button" as={Link} to='/admin' color={isLoggedIn? `green` : `grey` } style={loginButtonStyle}>
            <Icon className="ui user icon"></Icon>
        </Button>
    </div>
    );
}

export default InfoBar;
